  `uuid` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `category_tab` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '模板类型。关联 capital_template_categories 表 tabs 字段',
  `area_code` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '区域代码。当本字段 isnull 时，表示公共模板',
  `title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '模板名称',
  `version` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '版本号，便于保存历史版本(category_tab/area_code相同时，唯一)。建议格式：2020.01.001，解析：年.表示数据字段修改/位置改动.表示注释修改',
  `head` json NOT NULL COMMENT '表头配置(生成模板)。location-位置,en-因为标识。\n\n            [{\"location\":\"A1\",\"en\":null,\"name\":\"渑池小微权力大数据监督平台-村资产数据录入标准表\",\"colspan\":\"16\"}]\n            1、location-位置;\n            2、en-英文表示,数据验证规则;\n            3、name-中文,验证表头',
  `rules` json NOT NULL COMMENT '验证规则。参考 laravel 验证规则',
  `message` json NOT NULL COMMENT '验证错误时，返回的 message',
  `body` json NOT NULL COMMENT '字段存储方式。太复杂，先预留，暂未通过本字段进行数据存储',
  `start_line` tinyint NOT NULL COMMENT '开始读取行数',
  `sort` int NOT NULL DEFAULT 1000 COMMENT '排序。若后期有必要一种类型(category_tab相同)有多个导入模板，则可以进行排序',
  `status` tinyint NOT NULL DEFAULT 1 COMMENT '1-使用中，0-禁用',
  `remark` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '备注信息',
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  `deleted_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '批量导入模板表' ROW_FORMAT = Dynamic;

